// frontend/src/pages/ContactPage.js
import React, { useState } from "react";
import { submitContactForm } from "../services/apiService";
import { Send, User, Mail, MessageSquare } from "lucide-react"; // Icons

// Basic inline styles (consider moving to a CSS file or using Tailwind)
const styles = {
  pageContainer: {
    maxWidth: "700px",
    margin: "40px auto",
    padding: "30px",
    fontFamily: "'Inter', sans-serif",
    backgroundColor: "#fff",
    borderRadius: "12px",
    boxShadow: "0 8px 25px rgba(0,0,0,0.1)",
  },
  title: {
    textAlign: "center",
    fontSize: "2.5rem",
    fontWeight: "bold",
    color: "#333",
    marginBottom: "30px",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "20px",
  },
  inputGroup: {
    position: "relative",
  },
  label: {
    // Visually hidden label, but accessible
    position: "absolute",
    width: "1px",
    height: "1px",
    padding: "0",
    margin: "-1px",
    overflow: "hidden",
    clip: "rect(0,0,0,0)",
    whiteSpace: "nowrap",
    border: "0",
  },
  input: {
    width: "100%",
    padding: "12px 15px 12px 45px", // Padding for icon
    border: "1px solid #ccc",
    borderRadius: "8px",
    fontSize: "1rem",
    transition: "border-color 0.3s ease",
  },
  inputFocus: {
    // Apply this on focus
    borderColor: "#007bff", // Blue border on focus
    boxShadow: "0 0 0 0.2rem rgba(0,123,255,.25)",
  },
  textarea: {
    width: "100%",
    padding: "12px 15px 12px 45px", // Padding for icon
    border: "1px solid #ccc",
    borderRadius: "8px",
    fontSize: "1rem",
    minHeight: "150px",
    resize: "vertical",
    transition: "border-color 0.3s ease",
  },
  icon: {
    position: "absolute",
    left: "15px",
    top: "50%",
    transform: "translateY(-50%)",
    color: "#777",
  },
  button: {
    backgroundColor: "#007bff",
    color: "white",
    padding: "12px 20px",
    border: "none",
    borderRadius: "8px",
    fontSize: "1.1rem",
    fontWeight: "600",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "8px",
  },
  buttonHover: {
    // Apply on hover
    backgroundColor: "#0056b3",
  },
  message: {
    textAlign: "center",
    padding: "10px",
    borderRadius: "5px",
    marginTop: "20px",
  },
  successMessage: {
    backgroundColor: "#d4edda",
    color: "#155724",
  },
  errorMessage: {
    backgroundColor: "#f8d7da",
    color: "#721c24",
  },
};

const ContactPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });
  const [statusMessage, setStatusMessage] = useState("");
  const [isSuccess, setIsSuccess] = useState(null); // null, true, or false
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [focusedField, setFocusedField] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.name || !formData.email || !formData.message) {
      setStatusMessage("Please fill in all fields.");
      setIsSuccess(false);
      return;
    }
    setIsSubmitting(true);
    setStatusMessage("");
    try {
      const response = await submitContactForm(formData);
      if (response.success) {
        setStatusMessage(
          response.message || "Message sent successfully! Thank you."
        );
        setIsSuccess(true);
        setFormData({ name: "", email: "", message: "" }); // Clear form
      } else {
        setStatusMessage(
          response.message || "Failed to send message. Please try again."
        );
        setIsSuccess(false);
      }
    } catch (error) {
      setStatusMessage("An unexpected error occurred. Please try again later.");
      setIsSuccess(false);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div
      style={styles.pageContainer}
      className="max-w-2xl mx-auto my-10 p-6 md:p-8 bg-white rounded-xl shadow-xl font-sans"
    >
      <h1
        style={styles.title}
        className="text-3xl md:text-4xl font-bold text-gray-800 text-center mb-8"
      >
        Get In Touch
      </h1>
      <form onSubmit={handleSubmit} style={styles.form} className="space-y-6">
        <div style={styles.inputGroup} className="relative">
          <label htmlFor="name" style={styles.label}>
            Your Name
          </label>
          <User
            style={styles.icon}
            className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"
            size={20}
          />
          <input
            type="text"
            name="name"
            id="name"
            placeholder="Your Name"
            value={formData.name}
            onChange={handleChange}
            onFocus={() => setFocusedField("name")}
            onBlur={() => setFocusedField("")}
            style={{
              ...styles.input,
              ...(focusedField === "name" ? styles.inputFocus : {}),
            }}
            className="w-full p-3 pl-10 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition duration-150 ease-in-out"
            required
          />
        </div>

        <div style={styles.inputGroup} className="relative">
          <label htmlFor="email" style={styles.label}>
            Your Email
          </label>
          <Mail
            style={styles.icon}
            className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"
            size={20}
          />
          <input
            type="email"
            name="email"
            id="email"
            placeholder="Your Email Address"
            value={formData.email}
            onChange={handleChange}
            onFocus={() => setFocusedField("email")}
            onBlur={() => setFocusedField("")}
            style={{
              ...styles.input,
              ...(focusedField === "email" ? styles.inputFocus : {}),
            }}
            className="w-full p-3 pl-10 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition duration-150 ease-in-out"
            required
          />
        </div>

        <div style={styles.inputGroup} className="relative">
          <label htmlFor="message" style={styles.label}>
            Your Message
          </label>
          <MessageSquare
            style={{ ...styles.icon, top: "22px", transform: "translateY(0)" }}
            className="absolute left-3 top-3.5 text-gray-400"
            size={20}
          />
          <textarea
            name="message"
            id="message"
            placeholder="Your Message"
            value={formData.message}
            onChange={handleChange}
            onFocus={() => setFocusedField("message")}
            onBlur={() => setFocusedField("")}
            style={{
              ...styles.textarea,
              ...(focusedField === "message" ? styles.inputFocus : {}),
            }}
            className="w-full p-3 pl-10 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition duration-150 ease-in-out h-36 resize-none"
            required
          />
        </div>

        <button
          type="submit"
          style={styles.button}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-4 rounded-lg shadow-md transition-colors duration-300 flex items-center justify-center disabled:opacity-50"
          disabled={isSubmitting}
        >
          <Send size={20} className="mr-2" />
          {isSubmitting ? "Sending..." : "Send Message"}
        </button>
      </form>

      {statusMessage && (
        <div
          style={{
            ...styles.message,
            ...(isSuccess ? styles.successMessage : styles.errorMessage),
          }}
          className={`mt-6 p-3 rounded-md text-center ${
            isSuccess
              ? "bg-green-100 text-green-700"
              : "bg-red-100 text-red-700"
          }`}
        >
          {statusMessage}
        </div>
      )}
    </div>
  );
};

export default ContactPage;
