// frontend/src/pages/AboutPage.js
import React, { useState, useEffect } from "react";
import { getAboutInfo } from "../services/apiService";
import {
  Linkedin,
  Github,
  Mail,
  Phone,
  MapPin,
  Briefcase,
  Code,
} from "lucide-react"; // Using lucide-react for icons

// Basic inline styles (consider moving to a CSS file or using Tailwind)
const styles = {
  pageContainer: {
    padding: "20px",
    fontFamily: "'Inter', sans-serif", // Example font
  },
  loading: {
    textAlign: "center",
    fontSize: "1.5rem",
    color: "#555",
  },
  error: {
    textAlign: "center",
    fontSize: "1.5rem",
    color: "red",
  },
  profileSection: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    textAlign: "center",
    marginBottom: "40px",
    // Using Tailwind below for responsiveness
  },
  profileImage: {
    width: "180px",
    height: "180px",
    borderRadius: "50%",
    objectFit: "cover",
    marginBottom: "20px",
    boxShadow: "0 4px 15px rgba(0,0,0,0.1)",
  },
  name: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    margin: "10px 0 5px 0",
    color: "#333",
  },
  tagline: {
    fontSize: "1.3rem",
    color: "#555",
    marginBottom: "20px",
  },
  bio: {
    fontSize: "1.1rem",
    lineHeight: "1.8",
    color: "#444",
    maxWidth: "800px",
    margin: "0 auto 20px auto",
    textAlign: "left", // Or 'justify'
  },
  contactInfo: {
    display: "flex",
    justifyContent: "center",
    gap: "15px",
    flexWrap: "wrap", // Allow wrapping on smaller screens
    marginBottom: "30px",
  },
  contactItem: {
    display: "flex",
    alignItems: "center",
    gap: "8px",
    fontSize: "1rem",
    color: "#555",
  },
  socialLinks: {
    display: "flex",
    justifyContent: "center",
    gap: "20px",
    marginBottom: "30px",
  },
  socialLink: {
    color: "#333",
    fontSize: "1.8rem", // Icon size
  },
  skillsSection: {
    marginTop: "30px",
  },
  skillsTitle: {
    fontSize: "1.8rem",
    fontWeight: "600",
    marginBottom: "15px",
    textAlign: "center",
    color: "#333",
  },
  skillsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(150px, 1fr))", // Responsive grid
    gap: "15px",
  },
  skillItem: {
    backgroundColor: "#f0f0f0",
    padding: "10px 15px",
    borderRadius: "8px",
    textAlign: "center",
    boxShadow: "0 2px 5px rgba(0,0,0,0.05)",
  },
  skillName: {
    fontWeight: "500",
  },
  skillLevel: {
    fontSize: "0.9rem",
    color: "#666",
  },
};

const AboutPage = () => {
  const [aboutData, setAboutData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAboutData = async () => {
      try {
        setLoading(true);
        const data = await getAboutInfo();
        setAboutData(data);
        setError("");
      } catch (err) {
        console.error("API Error in AboutPage:", err);
        setError(
          "Failed to load about information. Please try refreshing the page."
        );
        // Set some default data on error to prevent crashes if data is null
        setAboutData({
          name: "N/A",
          bio: "Error loading content.",
          profileImageUrl:
            "https://placehold.co/150x150/FF0000/FFFFFF?text=Error",
          email: "",
          phone: "",
          location: "",
          socialLinks: [],
          skills: [],
        });
      } finally {
        setLoading(false);
      }
    };

    fetchAboutData();
  }, []);

  if (loading) {
    return (
      <div
        style={styles.loading}
        className="text-center py-10 text-xl text-gray-600"
      >
        Loading About Me...
      </div>
    );
  }

  if (error || !aboutData) {
    // Check !aboutData as well
    return (
      <div
        style={styles.error}
        className="text-center py-10 text-xl text-red-500"
      >
        {error || "About data is unavailable."}
      </div>
    );
  }

  const {
    name,
    tagline,
    bio,
    profileImageUrl,
    email,
    phone,
    location,
    socialLinks,
    skills,
  } = aboutData;

  const getSocialIcon = (platform) => {
    switch (platform.toLowerCase()) {
      case "linkedin":
        return <Linkedin size={28} />;
      case "github":
        return <Github size={28} />;
      // Add more cases for other platforms like Twitter, Instagram, etc.
      default:
        return null;
    }
  };

  return (
    <div
      style={styles.pageContainer}
      className="container mx-auto p-4 md:p-8 font-sans"
    >
      <section
        style={styles.profileSection}
        className="flex flex-col items-center text-center mb-12 md:flex-row md:text-left md:space-x-10"
      >
        <img
          src={
            profileImageUrl ||
            "https://placehold.co/180x180/E0E0E0/757575?text=Profile"
          }
          alt={name || "Profile"}
          style={styles.profileImage}
          className="w-36 h-36 md:w-48 md:h-48 rounded-full object-cover mb-6 md:mb-0 shadow-lg border-4 border-white"
          onError={(e) => {
            e.target.onerror = null;
            e.target.src =
              "https://placehold.co/180x180/FF0000/FFFFFF?text=Img+Error";
          }}
        />
        <div className="flex-grow">
          <h1
            style={styles.name}
            className="text-4xl md:text-5xl font-bold text-gray-800 mb-2"
          >
            {name || "Your Name"}
          </h1>
          {tagline && (
            <p style={styles.tagline} className="text-xl text-gray-600 mb-6">
              {tagline}
            </p>
          )}

          <div
            style={styles.contactInfo}
            className="flex flex-wrap justify-center md:justify-start gap-x-6 gap-y-3 mb-6 text-gray-700"
          >
            {email && (
              <a
                href={`mailto:${email}`}
                style={styles.contactItem}
                className="flex items-center hover:text-blue-600"
              >
                <Mail size={20} className="mr-2" /> {email}
              </a>
            )}
            {phone && (
              <span style={styles.contactItem} className="flex items-center">
                <Phone size={20} className="mr-2" /> {phone}
              </span>
            )}
            {location && (
              <span style={styles.contactItem} className="flex items-center">
                <MapPin size={20} className="mr-2" /> {location}
              </span>
            )}
          </div>

          {socialLinks && socialLinks.length > 0 && (
            <div
              style={styles.socialLinks}
              className="flex justify-center md:justify-start space-x-6 mb-6"
            >
              {socialLinks.map((link, index) => (
                <a
                  key={index}
                  href={link.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  aria-label={link.platform}
                  style={styles.socialLink}
                  className="text-gray-600 hover:text-blue-600 transition-colors duration-300"
                >
                  {getSocialIcon(link.platform) || link.platform}
                </a>
              ))}
            </div>
          )}
        </div>
      </section>

      {bio && (
        <section className="mb-12">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4 text-center md:text-left">
            About Me
          </h2>
          <p
            style={styles.bio}
            className="text-lg text-gray-700 leading-relaxed whitespace-pre-line"
          >
            {bio}
          </p>
        </section>
      )}

      {skills && skills.length > 0 && (
        <section style={styles.skillsSection} className="mb-12">
          <h2
            style={styles.skillsTitle}
            className="text-2xl font-semibold text-gray-800 mb-6 text-center"
          >
            <Code size={28} className="inline mr-2 align-middle" />
            Technical Skills
          </h2>
          <div
            style={styles.skillsGrid}
            className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4"
          >
            {skills.map((skill, index) => (
              <div
                key={index}
                style={styles.skillItem}
                className="bg-gray-100 p-4 rounded-lg shadow hover:shadow-md transition-shadow"
              >
                <h3
                  style={styles.skillName}
                  className="font-medium text-gray-700"
                >
                  {skill.name}
                </h3>
                {skill.level && (
                  <p
                    style={styles.skillLevel}
                    className="text-sm text-gray-500"
                  >
                    {skill.level}
                  </p>
                )}
              </div>
            ))}
          </div>
        </section>
      )}
    </div>
  );
};

export default AboutPage;
