// frontend/src/pages/ProjectsPage.js
import React, { useState, useEffect } from "react";
import { getProjects } from "../services/apiService";
import { ExternalLink, Github, Calendar, Layers } from "lucide-react"; // Icons

// Basic inline styles (consider moving to a CSS file or using Tailwind)
const styles = {
  pageContainer: {
    padding: "20px",
    fontFamily: "'Inter', sans-serif",
  },
  title: {
    textAlign: "center",
    fontSize: "2.5rem",
    fontWeight: "bold",
    color: "#333",
    marginBottom: "40px",
  },
  loading: {
    textAlign: "center",
    fontSize: "1.5rem",
    color: "#555",
  },
  error: {
    textAlign: "center",
    fontSize: "1.5rem",
    color: "red",
  },
  projectsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))", // Responsive grid
    gap: "30px",
  },
  projectCard: {
    backgroundColor: "#fff",
    border: "1px solid #e0e0e0",
    borderRadius: "12px",
    padding: "0", // No padding on card itself, handle inside
    boxShadow: "0 5px 15px rgba(0,0,0,0.08)",
    overflow: "hidden", // To contain image border radius
    display: "flex",
    flexDirection: "column",
    transition: "transform 0.3s ease, box-shadow 0.3s ease",
  },
  projectCardHover: {
    // Apply this on hover
    transform: "translateY(-5px)",
    boxShadow: "0 8px 25px rgba(0,0,0,0.12)",
  },
  projectImage: {
    width: "100%",
    height: "200px",
    objectFit: "cover",
    // borderTopLeftRadius: '12px', // If image is first element
    // borderTopRightRadius: '12px',
  },
  projectContent: {
    padding: "20px",
    flexGrow: 1, // Make content area grow
    display: "flex",
    flexDirection: "column",
  },
  projectTitle: {
    fontSize: "1.6rem",
    fontWeight: "600",
    color: "#383838",
    marginBottom: "10px",
  },
  projectDescription: {
    fontSize: "1rem",
    lineHeight: "1.6",
    color: "#555",
    marginBottom: "15px",
    flexGrow: 1, // Allow description to take available space
  },
  projectTech: {
    display: "flex",
    flexWrap: "wrap",
    gap: "8px",
    marginBottom: "15px",
  },
  techTag: {
    backgroundColor: "#e7f3ff", // Light blue
    color: "#0056b3", // Darker blue
    padding: "5px 10px",
    borderRadius: "20px",
    fontSize: "0.85rem",
    fontWeight: "500",
  },
  projectLinks: {
    display: "flex",
    gap: "15px",
    marginTop: "auto", // Push links to the bottom of the card
  },
  projectLink: {
    color: "#007bff", // Primary blue
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "5px",
    fontWeight: "500",
    transition: "color 0.2s ease",
  },
  projectLinkHover: {
    // Apply on hover for links
    color: "#0056b3", // Darker blue
  },
  dateInfo: {
    fontSize: "0.9rem",
    color: "#777",
    marginBottom: "10px",
    display: "flex",
    alignItems: "center",
    gap: "5px",
  },
};

const ProjectsPage = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [hoveredCard, setHoveredCard] = useState(null);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        setLoading(true);
        const data = await getProjects();
        setProjects(data || []); // Ensure projects is always an array
        setError("");
      } catch (err) {
        setError("Failed to load projects.");
        setProjects([]); // Set to empty array on error
      } finally {
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  const formatDate = (dateString) => {
    if (!dateString) return "Present";
    const options = { year: "numeric", month: "short" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <div
        style={styles.loading}
        className="text-center py-10 text-xl text-gray-600"
      >
        Loading Projects...
      </div>
    );
  }

  if (error) {
    return (
      <div
        style={styles.error}
        className="text-center py-10 text-xl text-red-500"
      >
        {error}
      </div>
    );
  }

  if (projects.length === 0) {
    return (
      <div className="text-center py-10 text-xl text-gray-600">
        No projects to display yet.
      </div>
    );
  }

  return (
    <div style={styles.pageContainer} className="container mx-auto p-4 md:p-8">
      <h1
        style={styles.title}
        className="text-3xl md:text-4xl font-bold text-gray-800 text-center mb-10"
      >
        My Projects
      </h1>
      <div
        style={styles.projectsGrid}
        className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
      >
        {projects.map((project) => (
          <div
            key={project._id || project.title} // Use _id from DB, fallback to title for default
            style={{
              ...styles.projectCard,
              ...(hoveredCard === project._id ? styles.projectCardHover : {}),
            }}
            className="bg-white rounded-lg shadow-lg overflow-hidden flex flex-col transition-all duration-300 hover:shadow-xl hover:-translate-y-1"
            onMouseEnter={() => setHoveredCard(project._id)}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <img
              src={
                project.imageUrl ||
                "https://placehold.co/600x400/EAEAEA/757575?text=Project"
              }
              alt={project.title}
              style={styles.projectImage}
              className="w-full h-48 object-cover"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src =
                  "https://placehold.co/600x400/FF0000/FFFFFF?text=Img+Error";
              }}
            />
            <div
              style={styles.projectContent}
              className="p-6 flex flex-col flex-grow"
            >
              <h2
                style={styles.projectTitle}
                className="text-2xl font-semibold text-gray-800 mb-2"
              >
                {project.title}
              </h2>
              {(project.startDate || project.endDate) && (
                <p
                  style={styles.dateInfo}
                  className="text-sm text-gray-500 mb-3 flex items-center"
                >
                  <Calendar size={16} className="mr-2 opacity-70" />
                  {formatDate(project.startDate)} -{" "}
                  {project.endDate ? formatDate(project.endDate) : "Present"}
                </p>
              )}
              <p
                style={styles.projectDescription}
                className="text-gray-700 leading-relaxed mb-4 flex-grow"
              >
                {project.description}
              </p>

              {project.technologies && project.technologies.length > 0 && (
                <div style={styles.projectTech} className="mb-4">
                  <h3 className="text-sm font-semibold text-gray-600 mr-2 inline-flex items-center">
                    <Layers size={16} className="mr-1 opacity-70" />{" "}
                    Technologies:
                  </h3>
                  <div className="flex flex-wrap gap-2">
                    {project.technologies.map((tech, index) => (
                      <span
                        key={index}
                        style={styles.techTag}
                        className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-xs font-medium"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>
              )}

              <div
                style={styles.projectLinks}
                className="mt-auto pt-4 border-t border-gray-200 flex items-center space-x-4"
              >
                {project.projectUrl && (
                  <a
                    href={project.projectUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={styles.projectLink}
                    className="text-blue-600 hover:text-blue-800 hover:underline font-medium inline-flex items-center"
                  >
                    <ExternalLink size={18} className="mr-1" /> View Project
                  </a>
                )}
                {project.sourceCodeUrl && (
                  <a
                    href={project.sourceCodeUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={styles.projectLink}
                    className="text-gray-600 hover:text-gray-800 hover:underline font-medium inline-flex items-center"
                  >
                    <Github size={18} className="mr-1" /> Source Code
                  </a>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProjectsPage;
